<?phpnamespace Meldon\WotRBundle\Entity;/** *  Dice rolling static class = returns object with results as an array and  *  total, can order ASC or DESC if requested */class Dice{	public static function roll( $number, $sides = 6, $order = NULL ) {		$roll = new \stdClass();		$roll->total = 0;		$roll->num_dice = $number;		for ( $i = 1; $i <= $number; $i++ )         {			$roll->total += $roll->results[$i] = mt_rand( 1, $sides );		}		if ( $order == "ASC" )         {			sort( $roll->results );		} elseif ( $order == "DESC" )        {			rsort( $roll->results );		}		return $roll;	}}?>